import React, { Component } from 'react';
import { GanttComponent, Inject, Selection, Toolbar, ExcelExport, PdfExport, ColumnsDirective, ColumnDirective } from '@syncfusion/ej2-react-gantt';
import { editingData, editingResources } from './data';
import DataFrame from "dataframe-js";

class Graph extends Component {

    componentWillReceiveProps(nextProps) {
        // used to pass the dataframe when loaded by parent page
        this.setState({ dataframe: nextProps.dataframe });  
    }

    constructor(props) {
        super(props)
        this.state = {
            dataframe: null,
            graph: [],
        }

        this.taskFields = {
            id: 'TaskID',
            name: 'TaskName',
            startDate: 'StartDate',
            endDate: 'EndDate',
            duration: 'Duration',
            progress: 'Progress',
            dependency: 'Predecessor',
            child: 'subtasks',
            resourceInfo: 'resources'
        };
        this.resourceFields = {
            id: 'resourceId',
            name: 'resourceName'
        };
        this.splitterSettings = {
            columnIndex: 2
        };
        this.projectStartDate = new Date('03/25/2019');
        this.projectEndDate = new Date('07/28/2019');
        this.gridLines = 'Both';
        this.toolbar = ['ExcelExport', 'CsvExport', 'PdfExport'];
        this.timelineSettings = {
            topTier: {
                unit: 'Week',
                format: 'MMM dd, y',
            },
            bottomTier: {
                unit: 'Day',
            },
        };
        this.labelSettings = {
            leftLabel: 'TaskName',
            rightLabel: 'resources'
        };
    }

    // {
    //     TaskID: 1,
    //     TaskName: 'Project initiation',
    //     StartDate: new Date('04/02/2019'),
    //     EndDate: new Date('04/21/2019'),
    //     subtasks: [

    async getGanttGraph(criterions) {
        if (this.state.dataframe == null)
            return []

        let df = this.getGanttInfoAsDataframe([
            "STR-1.07",
            "STR-1.C09",
            "STR-1.16",
            "STR-3.C06",
            "STR-3.C05",
            "STR-3.07"
        ])

        let cycleLifeCategories = [ 'Acquisition', 'Conception', 'Réalisation', 'Déploiement', 'Administration', 
            'Utilisation', 'Maintenance', 'Fin de Vie', 'Revalorisation' ]

        let graph = []
        let day = 1000*60*60*24
        let baseXPosition = new Date(2014, 2, 22)
        let widthOfCycleLife = 4
        let i = 1

        df.chain(row => {
            let cycleLifeIndex = cycleLifeCategories.indexOf(row.get("Etape Cycle de Vie"))
            let begXPosition = new Date(baseXPosition.getTime() + (widthOfCycleLife * cycleLifeIndex) * day)
            let endXPosition = new Date(baseXPosition.getTime() + (widthOfCycleLife * cycleLifeIndex) * day + widthOfCycleLife * day)

            graph.push({
                TaskID: i++,
                TaskName: row.get("Use Case"),
                StartDate: begXPosition,
                EndDate: endXPosition,
                subtasks: []
            })
            // graph.push([
            // row.get("CRITERES"),
            // row.get("Use Case"),
            // row.get("Etape Cycle de Vie"),
            // begXPosition,
            // endXPosition,
            // null,
            // 100,
            // null,
            // ])
            console.log(row.get("Etape Cycle de Vie"))
        })

        this.setState({
            graph: graph,
        })
        return graph
        }

    getGanttInfoAsDataframe(ids) {
        let dfs = []

        // for each id, fetch the required column infos 
        ids.forEach(id => {
            let df = this.state.dataframe.filter(row => row
            .get("ID") === id)
            .select("CRITERES", "Etape Cycle de Vie", "incontournables", "Use Case", "JUSTIFICATIONS", "Priorité");
            dfs.push(df)
        })

        // merge all the rows dataframes in one dataframe
        let totalDf = new DataFrame([]);
        dfs.forEach(df => {
            totalDf = totalDf.union(df)
        })

        // parse the priority to integers
        totalDf = totalDf.chain(this.translatePriorityToInteger)
        totalDf = this.sortDataframe(totalDf)
        return totalDf
        }

    toolbarClick(args) {
        if (args.item.id === "GanttExport_excelexport") {
            this.ganttInstance.excelExport();
        }
        else if (args.item.id === "GanttExport_csvexport") {
            this.ganttInstance.csvExport();
        }
        else if (args.item.id === "GanttExport_pdfexport") {
            this.ganttInstance.pdfExport();
        }
    }
    render() {
        console.log(this.state.dataframe)
        return (<div className='control-pane'>
        <div className='control-section'>
          <GanttComponent id='GanttExport' ref={gantt => this.ganttInstance = gantt} dataSource={this.graph} dateFormat={'MMM dd, y'} treeColumnIndex={1} allowExcelExport={true} allowPdfExport={true} allowSelection={true} showColumnMenu={false} highlightWeekends={true} allowUnscheduledTasks={true} projectStartDate={this.projectStartDate} projectEndDate={this.projectEndDate} splitterSettings={this.splitterSettings} taskFields={this.taskFields} timelineSettings={this.timelineSettings} labelSettings={this.labelSettings} toolbarClick={this.toolbarClick.bind(this)} height='410px' gridLines={this.gridLines} toolbar={this.toolbar} resourceFields={this.resourceFields} resources={[]}>
            <ColumnsDirective>
              <ColumnDirective field='TaskID' width='60'></ColumnDirective>
              <ColumnDirective field='TaskName' width='250'></ColumnDirective>
              <ColumnDirective field='StartDate'></ColumnDirective>
              <ColumnDirective field='EndDate'></ColumnDirective>
              <ColumnDirective field='Duration'></ColumnDirective>
              <ColumnDirective field='Predecessor'></ColumnDirective>
              <ColumnDirective field='resources'></ColumnDirective>
              <ColumnDirective field='Progress'></ColumnDirective>
            </ColumnsDirective>
            <Inject services={[Selection, Toolbar, ExcelExport, PdfExport]}/>
          </GanttComponent>
        </div>
      </div>);
    }
}

export default Graph;
